mapper.select:
select password
from customer
where id = #{id}

SELECT name
FROM customer
WHERE id=#{id} AND password=#{password}

mapper.update:
UPDATE customer
SET movie_count = movie_count+1
WHERE id = #{id}

UPDATE customer
SET animation_count = animation_count+1
WHERE id = #{id}

mapper.select:
SELECT animation_count 
FROM customer 
WHERE id = #{id}

SELECT movie_count 
FROM customer 
WHERE id = #{id}
